import pxToRem from"../../utils/pxToRem.js";const rootSelector="[data-js-expandable-content]";class ExpandableContent{selectors={root:rootSelector,button:"[data-js-expandable-content-button]"};stateClasses={isExpanded:"is-expanded"};animationParams={duration:300,easing:"ease"};constructor(t){this.rootElement=t,this.buttonElement=this.rootElement.querySelector(this.selectors.button),this.bindEvents()}onButtonClick=()=>{const{offsetHeight:t,scrollHeight:e}=this.rootElement;this.rootElement.classList.add(this.stateClasses.isExpanded),this.rootElement.animate([{maxHeight:`${pxToRem(t)}rem`},{maxHeight:`${pxToRem(e)}rem`}],this.animationParams)};bindEvents(){this.buttonElement.addEventListener("click",this.onButtonClick)}}class ExpandableContentCollection{constructor(){this.init()}init(){document.querySelectorAll(rootSelector).forEach((t=>{new ExpandableContent(t)}))}}export default ExpandableContentCollection;