export default class CloseHeaderPromo{static selectors={root:"[data-js-header]",headerPromo:"[data-js-header-promo]",closePromoButton:"[data-js-header-close-promo]"};static sessionStorageParams={wasClosedInCurrentSession:"wasClosedInCurrentSession"};constructor(){this.rootElement=document.querySelector(CloseHeaderPromo.selectors.root),this.bindEvents(),this.renderHeaderPromo()}onPromoButton=e=>{const o=e.target.closest(CloseHeaderPromo.selectors.closePromoButton);if(o){const e=o.closest(CloseHeaderPromo.selectors.headerPromo);e?.remove(),this.setClosedInCurrentSession()}};renderHeaderPromo(){Boolean(sessionStorage.getItem(CloseHeaderPromo.sessionStorageParams.wasClosedInCurrentSession))||this.rootElement.insertAdjacentHTML("afterbegin",this.headerPromoHTML())}bindEvents(){this.rootElement.addEventListener("click",this.onPromoButton)}setClosedInCurrentSession(){sessionStorage.setItem(CloseHeaderPromo.sessionStorageParams.wasClosedInCurrentSession,"true")}headerPromoHTML(){return'\n      <div class="header__promo" data-js-header-promo>\n        <button\n          class="header__promo-close button button--accent"\n          type="button"\n          aria-label="Close"\n          title="Close"\n          tabindex="0"\n          data-js-header-close-promo\n        >\n          X\n        </button>\n        <a class="header__promo-link" href="./contact.html" data-js-header-promo-link>\n          <span class="icon icon--arrow-top-right-yellow">\n            Subscribe to our Newsletter For New & latest Blogs and\n            Resources\n          </span>\n        </a>\n      </div>\n    '}}