class Posts{selectors={likeIcon:"[data-js-post-like-icon]",likeButton:"[data-js-post-like-button]"};stateClasses={isActive:"is-active"};constructor(){this.likeButtonElements=document.querySelectorAll(this.selectors.likeButton),this.bindEvents()}onClick=t=>{if(t?.target){const s=t.target.closest(this.selectors.likeButton);if(s){s.querySelector(this.selectors.likeIcon).classList.toggle(this.stateClasses.isActive)}}};bindEvents(){this.likeButtonElements.forEach((t=>{t.addEventListener("click",this.onClick)}))}}export default Posts;