const rootSelector="[data-js-video-player]";class VideoPlayer{selectors={root:rootSelector,video:"[data-js-video-player-video]",panel:"[data-js-video-player-panel]",playButton:"[data-js-video-player-play-button]"};stateClasses={isActive:"is-active"};constructor(e){this.rootElement=e,this.videoElement=this.rootElement.querySelector(this.selectors.video),this.panelElement=this.rootElement.querySelector(this.selectors.panel),this.playButtonElement=this.rootElement.querySelector(this.selectors.playButton),this.bindEvents()}onPlayButtonClick=()=>{this.videoElement.play(),this.videoElement.controls=!0,this.panelElement.classList.remove(this.stateClasses.isActive)};onVideoPause=()=>{this.videoElement.controls=!1,this.panelElement.classList.add(this.stateClasses.isActive)};bindEvents(){this.playButtonElement.addEventListener("click",this.onPlayButtonClick),this.videoElement.addEventListener("pause",this.onVideoPause)}}class VideoPlayerCollection{constructor(){this.init()}init(){document.querySelectorAll(rootSelector).forEach((e=>{new VideoPlayer(e)}))}}export default VideoPlayerCollection;